
function smartTable(table, title,columnArray){
  table.dataTable({
    paging:true,
    select:true,
    "bLengthChange": true,
    oLanguage: {
     sLengthMenu: "Show _MENU_ entries",
   },
   dom: '<"top"lf><"ml-5"B>rt<"bottom"<ip>>',
   buttons: [

   $.extend( true, {}, {
    extend: 'csvHtml5',
    title:title,
    exportOptions: {
      columns: columnArray
    },

  } ),

   $.extend( true, {}, {
    extend: 'excelHtml5',
    title:title,
    exportOptions: {
      columns: columnArray
    },

  } ),

   $.extend( true, {}, {
    extend: 'pdfHtml5',
    title:title,
    exportOptions: {
      columns: columnArray
    },

  } ),

   $.extend( true, {}, {
    extend: 'print',
    exportOptions:{
      columns:columnArray,
      modifier:{
        selected:null
      },
    },
    text:'print all',
    title:title,

  } ),

   $.extend( true, {}, {
    extend: 'print',
    text:'print selected',
    title:title,
     exportOptions: {
      columns: columnArray,
    },

  } ),


   ]


 });



}


