function AccountsTable(table, title, account, columnArray){
  table.dataTable({
    paging:true,
    select:true,
    "bLengthChange": true,
    oLanguage: {
     sLengthMenu: "Show _MENU_ entries",
   },
   dom: '<"top"lf><B>rt<"bottom"<ip>>',
   buttons: [

   $.extend( true, {}, {
    extend: 'pdfHtml5',
    exportOptions:{
      columns:columnArray,
      modifier:{
        selected:null
      }
    },
    text:'<i class="fa fa-download text-info"></i> '+account+' Savings A/C Statement',
    title:title,

  } ),

   $.extend( true, {}, {
    extend: 'print',
    exportOptions:{
      columns:columnArray,
      modifier:{
        selected:null
      }
    },
    text:'<i class="fa fa-print text-success"></i> Print '+account+' Savings A/C Statement',
    title:title,

  } ),
   ]
 });

}